catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var fromDate = g_form.getValue('u_from_date');


    var ga = new GlideAjax('global.AjaxDateUtil');
    ga.addParam('sysparm_name', 'isDateBeforeFrom');
    ga.addParam('sysparm_from', fromDate);
    ga.addParam('sysparm_to', newValue);
    ga.getXML(getAnswer);

    function getAnswer(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        if (newValue && fromDate != '') {
            if (answer == 'true') {
                g_form.clearValue('u_to_date');
                g_form.showFieldMsg('u_to_date', "Date cannot be before the 'From Date' field.", 'error');
            }
        }
    }
}
